import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

// Dummy quiz data
const quizData = [
  {
    id: 1,
    question: 'What is the capital of France?',
    options: ['Paris', 'Berlin', 'London', 'Rome'],
    correctAnswer: 'Paris'
  },
  {
    id: 2,
    question: 'Who wrote the play "Romeo and Juliet"?',
    options: ['William Shakespeare', 'Jane Austen', 'Leo Tolstoy', 'Fyodor Dostoevsky'],
    correctAnswer: 'William Shakespeare'
  }
];

// Main App component
function App() {
  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<Quiz />} />
          <Route path="/result" element={<Result />} />
          <Route path="*" element={<NotFound />} />
        </Routes>
      </div>
    </Router>
  );
}

// Quiz component
function Quiz() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState(Array(quizData.length).fill(''));
  const [submitted, setSubmitted] = useState(false);

  const handleAnswerChange = (e) => {
    const updatedAnswers = [...answers];
    updatedAnswers[currentQuestion] = e.target.value;
    setAnswers(updatedAnswers);
  };

  const handleSubmit = () => {
    setSubmitted(true);
  };

  const goToNextQuestion = () => {
    setCurrentQuestion((prevQuestion) => prevQuestion + 1);
  };

  if (submitted) {
    return <Navigate to="/result" />;
  }

  return (
    <div>
      <h2>Question {currentQuestion + 1}</h2>
      <p>{quizData[currentQuestion].question}</p>
      <form>
        {quizData[currentQuestion].options.map((option, index) => (
          <div key={index}>
            <label>
              <input
                type="radio"
                name={`question-${currentQuestion}`}
                value={option}
                checked={answers[currentQuestion] === option}
                onChange={handleAnswerChange}
              />
              {option}
            </label>
          </div>
        ))}
      </form>
      <button onClick={handleSubmit}>Submit</button>
      {currentQuestion < quizData.length - 1 && <button onClick={goToNextQuestion}>Next</button>}
    </div>
  );
}

// Result component
function Result() {
  const totalQuestions = quizData.length;
  const totalCorrect = quizData.reduce((acc, question, index) => {
    return acc + (question.correctAnswer === answers[index] ? 1 : 0);
  }, 0);

  return (
    <div>
      <h2>Quiz Result</h2>
      <p>Total Questions: {totalQuestions}</p>
      <p>Total Correct Answers: {totalCorrect}</p>
    </div>
  );
}

// NotFound component
function NotFound() {
  return <h2>404 - Not Found</h2>;
}

export default App;
