    const [join,setJoin]=useState(true);
    const [text,setText]=useState("Create a Room");
    const [submission,setSubmission]=useState(false);
    const navigate = useNavigate();

    const [openPicker, authResponse] = useDrivePicker();  
    const handleOpenPicker = () => {
            openPicker({
            clientId: "51460907741-bsckge5dghc0i68evh2o7s32ljr83klj.apps.googleusercontent.com",
            developerKey: "AIzaSyC2i0U6sQXh8ZA5aKLZool2Q40IlE5bEmA",
            viewId: "DOCS",
            showUploadView: true,
            showUploadFolders: true,
            supportDrives: true,
            multiselect: false,
            callbackFunction: (data) => {
                if (data.action === 'cancel') {
                    console.log('User clicked cancel/close button')
                    return;
                }
                if(data.docs && data.docs.length > 0)
                {
                    setUrl(data.docs[0].url);
                    setPdfId(data.docs[0].url);
                    navigate('/main');
                }
            },
        })
    }

    function handleCreate(event){
        event.preventDefault();
        setCreate(false);
        const croom=document.getElementById('croom');
        croom.classList.add('animateButton');
        async function getToken() {
            const formData = {
                username: userData.username,
            };
            await fetch(`http://192.168.1.8:5000/generateToken`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(formData)
            })
            .then(response => {
                if (response.status===401) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                setText("Share this Token : "+data.token);

                console.log("User data retrieved successfully:", data);
            })
            .catch(error => {
                console.error("Error retrieving user data:", error);
            });
        }
        getToken();
    }

    function handleStart(event){
        event.preventDefault();
        setStatus(402);
        async function start() {
            const formData = {
                username: userData.username,
                status:status,
                chStatus:chStatus
            };
            console.log(formData);
            await fetch(`http://192.168.1.8:5000/start2player`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(formData)
            })
            .then(response => {
                if (response.status===401) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                console.log("User data retrieved successfully:", data);
                setStatus(parseInt(data.status));
                if(data.status==200)
                {
                    handleOpenPicker();
                    setChStatus(true);
                }
            })
            .catch(error => {
                console.error("Error retrieving user data:", error);
            });
        }
        start();
    }

    function handleJoin(event){
        event.preventDefault();
        if(status!==400)
            return;
        setStatus(402);
        const tokenId=document.getElementById('tokenid');
        async function sendToken() {
            const formData = {
                token:tokenId.value
            };
            await fetch(`http://192.168.1.8:5000/verifyToken`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(formData)
            })
            .then(response => {
                if (response.status===401) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                console.log("User data retrieved successfully:", data);
                setDummy(data.questions);
                setSubmission(true);
                setStatus(parseInt(data.status));
            })
            .catch(error => {
                console.error("Error retrieving user data:", error);
            });
        }
        sendToken();
    }

    if(submission)
    return <Navigate to="/testpage" />

    return (
        <div className="multiplayer">
            <div className="multiplay">
                <h1>PICK ONE</h1>
                <table>
                    <tr>
                        {join && <td><button id="croom" onClick={text==="Create a Room" ? handleCreate:null}>{text}</button><button onClick={handleStart}>{status===400?"Start":"Second Player has not joined yet..."}</button></td>}
