import { useState } from 'react';
import './app.css';
let dummy=[
    {
        id:0,
        question: "When was the National Centre for Good Governance (NCGG) set up by the Government of India?",
        option1: "1995",
        option2: "2014",
        option3: "2005",
        option4: "2020",
        correct_option: "B",
        answered:0,
        chosen:"",
        summary: "The National Centre for Good Governance (NCGG) was set up in 2014 by the Government of India."
    },
    {
        id:1,
        question: "When was World Athletics Day instituted by the International Amateur Athletic Federation (IAAF)?",
        option1: "1990",
        option2: "1996",
        option3: "2000",
        option4: "2010",
        correct_option: "B",
        answered:0,
        chosen:"",
        summary: "World Athletics Day was instituted in 1996 by the International Amateur Athletic Federation (IAAF) to raise awareness of the physiological and psychological significance of sports."
    },
    {
        id:2,
        question: "When was the India Pavilion at Paper Arabia 2023 inaugurated?",
        option1: "May 14, 2023",
        option2: "May 16, 2023",
        option3: "May 18, 2023",
        option4: "May 20, 2023",
        correct_option: "B",
        answered:0,
        chosen:"",
        summary: "The India Pavilion at Paper Arabia 2023 was inaugurated on May 16, 2023, featuring 30 Indian companies."
    },
    {
        id:3,
        question: "Who was elected as the 1st-ever Indian-origin Lord Mayor of Birmingham?",
        option1: "Chaman Kumar",
        option2: "Chaman Singh",
        option3: "Chaman Lal",
        option4: "Chaman Raj",
        correct_option: "C",
        answered:0,
        chosen:"",
        summary: "Chaman Lal was elected as the 1st-ever Indian-origin Lord Mayor of Birmingham."
    }
];

export default function App(){
    const [curr,setCurr]=useState(0);
    const [selection,setSelection]=useState(new Array(dummy.length).fill(null));
    return (
        <div className="quiz">
            <Navbar curr={curr} setCurr={setCurr}/>
            <Question curr={curr} setCurr={setCurr} selection={selection} setSelection={setSelection}/>
            <Options curr={curr} setCurr={setCurr}/>
        </div>
    );
}

function Navbar({curr,setCurr}) {
    return (
        <div className="navbar">
            <ul>
                {dummy.map(element => (
                    <li className={curr===element.id?"nav_selected":""} key={element.id} style={{ border: '2px solid', borderColor: element.answered === 0 ? 'gray' : element.answered === 1 ? 'green' : element.answered === 2 ? 'purple' : 'red' }}>
                        {element.id+1}
                    </li>
                ))}
            </ul>
        </div>
    );
}

function Question({ curr, setCurr, selection, setSelection}) {
    const [selectedAnswer, setSelectedAnswer] = useState(null);

    const handleAnswerChange = (event) => {
        const selectedValue = event.target.value;
        setSelectedAnswer(selectedValue);
        dummy[curr].chosen = selectedValue;
    };

    return (
        <div className="question">
            <p>Q. {dummy[curr].question}</p>
            <div className='options'>
                <div className='labels'><label>
                    <input
                        type="radio"
                        name="answer"
                        value="A"
                        checked={selectedAnswer === "A" || dummy[curr].chosen === "A"}
                        onChange={handleAnswerChange}
                    />
                    {dummy[curr].option1}
                </label></div>
                <br />
                <div className='labels'><label>
                    <input
                        type="radio"
                        name="answer"
                        value="B"
                        checked={selectedAnswer === "B" || dummy[curr].chosen === "B"}
                        onChange={handleAnswerChange}
                    />
                    {dummy[curr].option2}
                </label></div>
                <br />
                <div className='labels'><label>
                    <input
                        type="radio"
                        name="answer"
                        value="C"
                        checked={selectedAnswer === "C" || dummy[curr].chosen === "C"}
                        onChange={handleAnswerChange}
                    />
                    {dummy[curr].option3}
                </label></div>
                <br />
                <div className='labels'><label>
                    <input
                        type="radio"
                        name="answer"
                        value="D"
                        checked={selectedAnswer === "D" || dummy[curr].chosen === "D"}
                        onChange={handleAnswerChange}
                    />
                    {dummy[curr].option4}
                </label></div>
            </div>
        </div>
    );
}
function Options({curr,setCurr}){
    function previous()
    {
        if(curr>0)
        setCurr(curr-1);
    }
    function review()
    {
        if (dummy[curr]) {
            dummy[curr].answered = 2;
        }
    }
    function next()
    {
        if(curr<dummy.length-1)
        setCurr(curr+1);
    }
    return (
        <div className='choices_bar'>
            <ul className='choices'>
                <li className='prev'><button onClick={previous}>Previous</button></li>
                <li className='review'><button onClick={review}>Mark for Review</button></li>
                <li className='next'><button onClick={next}>Next</button></li>
            </ul>
        </div>
    );
}
