import React, { useState } from 'react';
import "./app.css"

export default function App(){

    const [statetype,setStatetype]=useState(true);

    function handleSubmit(event) {
        event.preventDefault();
        const formData = {
            username: event.target.username.value,
            password: event.target.password.value
        };
        fetch(`http://localhost:3001/api/login`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(formData)
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then(data => {
            console.log("User data retrieved successfully:", data);
        })
        .catch(error => {
            console.error("Error retrieving user data:", error);
        });
    }


    return (
        <div className="auth">
            <div className='leftauth'>
                {statetype && <form onSubmit={handleSubmit}>
                    <table border={"2px solid white"}>
                        <tr>
                            <td><input type="username" name="username" placeholder="Enter Username..." required/></td>
                            <td><input type="password" name="password" placeholder="Enter Password..." required/></td>
                        </tr>
                        <tr>
                            <td colSpan={2}><button type="submit">Submit</button></td>
                        </tr>
                    </table>
                </form>}
                {!statetype && <form>
                    <input type='fname' name='fname' placeholder='Enter First Name...' required/>
                    <input type='lname' name='lname' placeholder='Enter Last Name...' required/>
                    <input type='username' name='username' placeholder='Enter Username...' required/>
                    <input type='password' name='password' placeholder='Enter Password...' required/>
                    <button type='submit'>Submit</button>
                </form>}
            </div>
            <div className='rightauth'>

            </div>
        </div>
    );
}
