function Navbar({curr,setCurr,userData,pdfId,dummy}) {
    const [submission,setSubmission] = useState(false);
    const [confirmation,setConfirmation]=useState(true);
    const [count,setCount]=useState(0);

    function handleTest() {
        let corr = 0;
        dummy.forEach((element) => {
            if (element.chosen === element.correct_option) corr += 1;
        });
        const today = new Date();
        const dd = String(today.getDate()).padStart(2, '0'); 
        const mm = String(today.getMonth() + 1).padStart(2, '0'); 
        const yyyy = today.getFullYear(); 
        const formData = {
            userId: userData._id,
            testDate:dd+" / "+mm+" / "+yyyy,
            score: corr,
            passed:(corr>3?true:false),
            remarks: "good",
            pdfFileId: pdfId
        };
        fetch(`http://localhost:5002/api/createTest`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(formData)
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            console.log("Test data sent successfully");
        })
        .catch(error => {
            console.error("Error sending test data:", error);
        });
    }    

    function handleSubmit(event){
        event.preventDefault();
        handleTest();
        let c=0;
        dummy.forEach((element)=>{
            if(element.answered===2)
            c+=1;
        });
        setCount(c);
        if(c)
        setConfirmation(false);
        setSubmission(true);
    }

    if(submission && confirmation)
    return <Navigate to="/result" />

    return (
        <div className="navbar">
            {confirmation? <ul>
                {dummy.map((element) => (
                    <li onClick={()=>{setCurr(element.id)}} className={curr===element.id?"nav_selected":""} key={element.id} style={{ border: '2px solid', borderColor: element.answered === 0 ? 'gray' : element.answered === 1 ? 'green' : element.answered === 2 ? 'purple' : 'red' }}>
                        {element.id+1}
                    </li>
                ))}
                <li className='submission'><button onClick={handleSubmit}>Submit</button></li>
            </ul>:<ul><li style={{gridColumn:4,width:"500px"}}>You have {count} questions marked for review. Proceed?</li><li><button onClick={()=>{setConfirmation(true);}}>Proceed</button></li><li><button onClick={()=>{setSubmission(false);setConfirmation(true);}}>Cancel</button></li></ul>}
        </div>
    );
}

function Question({ curr, setCurr,dummy}) {
    const [selectedAnswer, setSelectedAnswer] = useState(null);

    const handleAnswerChange = (event) => {
        const selectedValue = event.target.value;
        setSelectedAnswer(selectedValue);
        dummy[curr].chosen = selectedValue;
        dummy[curr].answered=1;
    };

    return (
        <div className="question">
            <p>Q. {dummy[curr].question}</p>
            <div className='options'>
                <div className='labels'><label>
                    <input
                        type="radio"
                        name="answer"
                        value="A"
                        checked={dummy[curr].chosen === "A"}
                        onChange={handleAnswerChange}
                    />
                    {dummy[curr].option1}
                </label></div>
                <br />
                <div className='labels'><label>
                    <input
                        type="radio"
                        name="answer"
                        value="B"
                        checked={dummy[curr].chosen === "B"}
                        onChange={handleAnswerChange}
                    />
                    {dummy[curr].option2}
                </label></div>
                <br />
                <div className='labels'><label>
                    <input
                        type="radio"
                        name="answer"
                        value="C"
                        checked={dummy[curr].chosen === "C"}
                        onChange={handleAnswerChange}
                    />
                    {dummy[curr].option3}
                </label></div>
                <br />
                <div className='labels'><label>
                    <input
                        type="radio"
                        name="answer"
                        value="D"
                        checked={dummy[curr].chosen === "D"}
                        onChange={handleAnswerChange}
                    />
                    {dummy[curr].option4}
                </label></div>
            </div>
        </div>
    );
}
function Options({curr,setCurr,dummy}){
    function previous()
    {
        if(curr>0)
        setCurr(curr-1);
        if(dummy[curr].answered===0)
            dummy[curr].answered=3;
    }
    function review()
    {
        if (dummy[curr]) {
            dummy[curr].answered = dummy[curr].answered===2?0:2;
        }
    }
    function next()
    {
        if(curr<dummy.length-1)
        {
            setCurr(curr+1);
            if(dummy[curr].answered===0)
            dummy[curr].answered=3;
        }
    }
    return (
        <div className='choices_bar'>
            <ul className='choices'>
                <li className='prev'><button onClick={previous}>Previous</button></li>
                <li className='review'><button onClick={review}>{dummy[curr].answered!==2?"Mark for Review":"Unmark from Review"}</button></li>
                <li className='next'><button onClick={next}>Next</button></li>
            </ul>
        </div>
    );
}

function MultiPlayer({userData}){

    const [create,setCreate]=useState(true);
    const [join,setJoin]=useState(true);
    const [text,setText]=useState("Create a Room");

    function handleCreate(event){
        event.preventDefault();
        setCreate(false);
        const croom=document.getElementById('croom');
        croom.classList.add('animateButton');
        async function getToken() {
            const formData = {
                username: userData.username,
            };
            await fetch(`http://127.0.0.1:3005/generateToken`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(formData)
            })
            .then(response => {
                if (response.status===401) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                setText("Share this Token : "+data.token);

                console.log("User data retrieved successfully:", data);
            })
            .catch(error => {
                console.error("Error retrieving user data:", error);
            });
        }
        getToken();
    }

    function handleJoin(event){
        event.preventDefault();
        const tokenId=document.getElementById('tokenid');
        async function sendToken() {
            const formData = {
                token:tokenId.value
            };
            await fetch(`http://127.0.0.1:3005/verifyToken`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(formData)
            })
            .then(response => {
                if (response.status===401) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                console.log("User data retrieved successfully:", data);
            })
            .catch(error => {
                console.error("Error retrieving user data:", error);
            });
        }
        sendToken();
    }

    return (
        <div className="multiplayer">
            <div className="multiplay">
                <h1>PICK ONE</h1>
                <table>
                    <tr>
                        {join && <td><button id="croom" onClick={handleCreate}>{text}</button></td>}
                        {create && <td>{join ? <button id="jroom" onClick={()=>setJoin(false)}>Join a Room</button>:<form onSubmit={handleJoin}><input id="tokenid" placeholder="Enter Token Number..." style={{height:"50px",width:"100%",marginBottom:"10px",borderRadius:"20px"}} type="text" required/><button type="submit" style={{height:"50px"}}>Submit</button></form>}</td>}
                    </tr>
                </table>
            </div>
        </div>
    );
}

function Essay({ desdummy, curr }) {
    const [value, setValue] = useState(desdummy[curr].chosen);

    useEffect(() => {
        setValue(desdummy[curr].chosen);
    }, [curr, desdummy]);

    function handleTextareaChange(event) {
        const newValue = event.target.value;
        setValue(newValue);
        desdummy[curr].chosen = newValue;
        desdummy[curr].answered = 1;
    }

    return (
        <div className="question">
            <p>Q. {desdummy[curr].question}</p>
            <textarea placeholder="Enter your answer..." value={value} onChange={handleTextareaChange}></textarea>
        </div>
    );
}

function Queries() {
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');

    const handleMessageSubmit = async (event) => {
        event.preventDefault();
        if (newMessage.trim() === '') return;

        const userMessage = { text: newMessage, sender: 'user' };
        setMessages(prevMessages => [...prevMessages, userMessage]);
        setNewMessage('');

        try {
            const formData = { "query": newMessage };
            const response = await fetch(`http://localhost:3005/query`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(formData)
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            const modelMessage = { text: data.answer, sender: 'model' };
            setMessages(prevMessages => [...prevMessages, modelMessage]);
            console.log("User data retrieved successfully:", data);
        } catch (error) {
            console.error("Error retrieving user data:", error);
        }
    };    

    const handleChange = (event) => {
        setNewMessage(event.target.value);
    };

    useEffect(() => {
        if (messages.length === 0) {
            new Typewriter('#typewriter', {
                strings: ['Start by saying Hello...','Analyze your PDF'],
                autoStart: true,
                delay: 75,
                loop: true
            });
        }
    }, [messages]);

    return (
        <div className="queries">
            <div className="query">
                <h1>Clear your Doubts...</h1>
                {messages.length!==0 && (<div className="messages">
                    {messages.map((message, index) => (
                        <div key={index} className={`message ${message.sender}`}>
                            <p>{message.text}</p>
                        </div>
                    ))}
                </div>)}
                {messages.length===0&&<div className="msgs_bg" id="typewriter"></div>}
                <form onSubmit={handleMessageSubmit} className="msgs_input">
                    <input type="text" placeholder="Type a message..." value={newMessage} onChange={handleChange} />
                    <button type="submit">Send</button>
                </form>
            </div>
        </div>
    );
}
